analysis.block:
begin paup;
log file=paup.log replace=yes;
exclude gapped;
export format=nexus file=paup.nex replace=yes;
set criterion=distance rootmethod=midpoint;
outgroup Spondweni_virus_SM6;
dset distance=HKY NegBrLen=SetAbsVal;
constraints CONSTRE=${BACKBONE-CLUSTERS};
NJ constraints=CONSTRE;
savetree format=nexus brlens=yes file=paup.tre replace=yes;
bootstrap nreps=1000 search=nj;
end;
quit;

begin paup;
log file=paup.log replace=yes;
exclude gapped;
export format=nexus file=paup.nex replace=yes;
set rootmethod=midpoint;
set criterion=distance rootmethod=midpoint;
dset distance=HKY NegBrLen=SetAbsVal;
NJ; savetree format=phylip brlens=yes file=paup.tre replace=yes;
bootstrap nreps=100 search=nj;
end;
quit;
